import firebase from 'firebase'
import 'firebase/auth'
import 'firebase/firestore'

const firebaseConfig = {
    apiKey: "AIzaSyDPYm43X6BRPZd6ne8oh7KVyiQoiHkj7tY",
    authDomain: "carculator-app.firebaseapp.com",
    projectId: "carculator-app",
    storageBucket: "carculator-app.appspot.com",
    messagingSenderId: "253767789954",
    appId: "1:253767789954:web:91100a5dcae56a2d7ef39b"
  };
let app;
if(firebase.apps.length ===0){
      app = firebase.initializeApp(firebaseConfig)
}else{
      app = firebase.app()
}
const db = app.firestore()
const auth = firebase.auth()
export {db,auth}
